import React, { Component } from "react";
import fire from '../../FirebaseConfig';

import './SignUp.css';
import Inicio from '../chat/Inicio';
import Login from '../chat/Login';

export default class SignUp extends Component{
  constructor(props){
    super(props);
    this.state={
      users: {}
    }
  }
  componentDidMount(){
    this.authListener();
  }
  authListener(){
    fire.auth().onAuthStateChanged((users) => {
      if(users){
        this.setState({users})
      }else{
        this.setState({users: null})
      }
    });
  }
  render(){
    return (

      <div>
        {this.state.user? (<Inicio/>) : (<Login/>)}
      </div>

  );
  }
}
