import React from 'react';

const Login = (props) => {
    const {
        email, 
        setEmail, 
        password, 
        setPassword, 
        handleLogin, 
        handleSignup, 
        hasAccount,
        setHasAccount,
        emailError, 
        passwordError
    } = props;
    
    return (
        <section className="login" >
            <div className="login_Container">
                <label>Email Address</label>
                <input 
                type="email"
                autoFocus
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                />
                <p className="errorMsg">{emailError}</p>
                <label>Password</label>
                <input 
                type="password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                />
                <p className="errorMsg">{passwordError}</p>
                <div className="btnContainer">
                    {hasAccount ? (
                        <>
                            <button  onClick={handleLogin}>Sign in</button>
                            <p className="button_signin">Don't have an count? <span onClick={() => setHasAccount(!hasAccount)}>Sign up</span></p>
                        </>
                    ):(
                        <>
                            <button onClick={handleSignup}>Sign Up</button>
                            <p className="button_signin">have an count?<span onClick={() => setHasAccount(!hasAccount)}>Sign in</span></p>
                        </>
                    )}
                </div>
            </div>
        </section>
    )
}

export default Login;
