import firebase from 'firebase';
import fire from "../../FirebaseConfig";

export default class Login extends Component {
    constructor(props){
        super(props)
        this.login =this.login.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state={
            email: " ",
            password: " "
        }
    }
