    }
    return (
        <div className="chat">
            <div className="chat__header">
                <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
                <div className="chat__haderInfo">
                    <h3>Alexander</h3>
                    <p>Last see at...</p>
                </div>
                <div className="chat__headerRight">
                    <IconButton>
                        <SearchOutlined/>
                    </IconButton>
                    <IconButton>
                        <AttachFile />
                    </IconButton>
                    <IconButton>
                        <MoreVert/>    
                    </IconButton>
                </div>
            </div>
            <div className="chat__body">
                <p className={`chat_message ${true && "chat__reciever"}`}>
                    Hey guys 
                    <span className="chat__name">
                        Sonny 
                    </span>
                    <span className="chat__timestamp">
                        3:35 pm
                    </span>
                </p>
            </div>
            <div className="chat__footer">
                <InsertEmoticonIcon />
                <form>
                    <input value={input} 
                    onChange={(e) => setInput(e.target.value)}
                    type="text"
                    placeholder="Type a message"
                    />
                    <button onClick={sendMessage} type="submit">Send a Message</button>
                </form>
                <MicIcon />
            </div>
        </div>
    );
}

export default Chat;
